View.ClientEvent:
$.cookie.json = true;
var pathMap = {};
window.openTab = function (caption, icon, path) {
	var url;
	if (caption instanceof dorado.Entity) {
		url = caption;
		caption = url.get("name");
		icon = url.get("icon");
		path = url.get("path");
	}
	if (!path) {
		if (url) {
			dsPart.setData(url.get("children").toJSON());
			dialogPart.show();
		}
		return;
	} else {
		var tab = pathMap[path];
		if (!tab) {
			tab = new dorado.widget.IFrame({
				path: path
			});
			cardBook.addControl(tab);
			pathMap[path] = tab;
			tab.refreshTime = new Date();

		}
		recordVisit(caption, icon, path);
		cardBook.set("currentControl", tab);
		appName.set("text", caption);
	}


};

window.refreshTab = function (caption, icon, path) {
	var url;
	if (caption instanceof dorado.Entity) {
		url = caption;
		caption = url.get("name");
		icon = url.get("icon");
		path = url.get("path");
	}
	var tab = pathMap[path];
	if (tab) {
		if (tab) {
			if (tab instanceof dorado.widget.tab.IFrameTab) {
				tab.get("control").reload();
			} else {
				tab.refresh();
			}
		}
	}
}

var recordVisit = function(caption, icon, path) {
	var vh = $.cookie("visitHistory");
	if (!vh) {
		vh = [];
	} 
	vh.each(function(item) {
		if (item.path === path) {
			vh.remove(item);
			return false;
		}
	});
	vh.unshift({
		caption: caption,
		icon: icon,
		path: path
	});
	if (vh.length > 10) {
		vh.pop();
	}
	$.cookie('visitHistory', vh, { expires: 1000, path: window.location.pathname});
	refreshVisit();
};

var refreshVisit = function() {
	historyContainer.removeAllChildren();
	var vh = $.cookie("visitHistory");
	if (!vh || vh.length === 0) {
		historyContainer.set("height", 0);
		return;
	}
	historyContainer.set("height", 35);
	var children = [];
	children.push({
		$type: "Label",
		exClassName: "clear-placeholder fa fa-times-circle"
	});
	vh.each(function(item) {
		children.push({
			$type: "Label",
			exClassName: "history",
			text: item.caption,
			onClick: function() {
				window.openTab(item.caption, item.icon, item.path);
			}
		});
	});
	children.push({
		$type: "Label",
		exClassName: "clear-history fa fa-times-circle",
		onClick: function() {
			$.removeCookie("visitHistory", {path: window.location.pathname});
			historyContainer.removeAllChildren();
		    historyContainer.set("height", 0);
		}
	});
	historyContainer.set("children", children);
};

self.refreshIconRefresh = function() {
	var c = cardBook.get("currentControl");
	if (c.refreshTime) {
		var now = new Date();
		if (now - c.refreshTime > 18000000) { // 大于5小时
			iconRefresh.set("exClassName", "icon refresh-black");
		} else if (now - c.refreshTime > 7200000) { // 大于2小时
			iconRefresh.set("exClassName", "icon refresh-red");
		} else if (now - c.refreshTime > 1800000) { // 大于30分钟
			iconRefresh.set("exClassName", "icon refresh-yellow");
		} else {
			iconRefresh.set("exClassName", "icon refresh");
		}
	} else {
		iconRefresh.set("exClassName", "icon refresh");
	}
};

refreshVisit();

setInterval(self.refreshIconRefresh, 5000);

Chat.addReceiveListener(function(c, arg) {
	if (self.refreshNoticeCountTask != null) {
		clearTimeout(self.refreshNotifyTotalTask);
	}
	self.refreshNoticeCountTask = setTimeout(function() {
		if (Chat.noticeCount) {
			$(".notify-total").css("display",  "inline-block").text(Chat.noticeCount);
			if (Chat.noticeCount > 9) {
				$(".notify-total").css("padding",  "2px 5px");
			} else {
				$(".notify-total").css("padding",  "2px");
			}
		} else {
			$(".notify-total").css("display",  "none").text(0);
		}
		self.refreshNoticeCountTask = null;
	}, 80);
});

Chat.addChatCloseListener(function() {
	Chat.hide();
});

DataSet.ClientEvent:
self.listUrls = [];
var parseUrls = function(urls, result) {
	if (urls.entityCount <= 0) {
		return;
	}
	urls.each(function(url) {
		result.push(url.toJSON());
		parseUrls(url.get("children"), result);
	});
};

setTimeout(function() {
	parseUrls(self.getData(), self.listUrls);
	dsQuery.setData(self.listUrls);
}, 10);

