Button.ClientEvent:
var newPassword = autoFormPassword.get("entity").get("newPassword");
var entity = autoFormPassword.get("entity");

entity.set("confirmNewPassword", entity.get("confirmNewPassword"));
entity.set("newPassword", entity.get("newPassword"));
entity.set("oldPassword", entity.get("oldPassword"));


if (entity.get("oldPassword") && entity.get("newPassword") && entity.get("confirmNewPassword") && autoFormPassword.validate(true) === true) {
	ajaxActionChangePassword.set("parameter", {
		username: "${loginUsername}",
		newPassword: newPassword
	}).execute(function() {
		self.get("parent.parent").hide();
		autoFormPassword.get("entity").reset();
	});
} else {
	dorado.MessageBox.alert("验证不通过，请检查。");
}
